  /* Radius */
  --radius: 0.75rem;
  --radius-sm: 0.5rem;
  --radius-md: 0.625rem;
  --radius-lg: 0.75rem;
  --radius-xl: 1rem;

  /* Primary - Deep Indigo */
  --primary: hsl(234 89% 54%);
  --primary-glow: hsl(234 89% 64%);
  --primary-foreground: hsl(0 0% 100%);

  /* Backgrounds */
  --background: hsl(220 20% 98%);
  --foreground: hsl(222 47% 11%);

  /* Cards */
  --card: hsl(0 0% 100%);
