  /* Muted */
  --muted: hsl(220 14% 96%);
  --muted-foreground: hsl(220 9% 46%);

  /* Accent */
  --accent: hsl(220 14% 96%);
  --accent-foreground: hsl(222 47% 11%);

  /* Destructive */
  --destructive: hsl(0 84% 60%);
  --destructive-foreground: hsl(0 0% 100%);

  /* Success */
  --success: hsl(142 76% 36%);
  --success-foreground: hsl(0 0% 100%);

