  --warning: hsl(38 92% 50%);
  --warning-foreground: hsl(0 0% 100%);

  /* Border & Input */
  --border: hsl(220 13% 91%);
  --input: hsl(220 13% 91%);
  --ring: hsl(234 89% 54%);

  /* Chart colors */
  --chart-1: hsl(234 89% 54%);
  --chart-2: hsl(262 83% 58%);
  --chart-3: hsl(142 76% 36%);
  --chart-4: hsl(38 92% 50%);
  --chart-5: hsl(340 82% 52%);

  /* Sidebar */
